import React, { useReducer } from 'react';
import MonitorContext from './monitorContext';
import MonitorReducer from './monitorReducer';
import axios from 'axios'
import {message} from 'antd'

import { GET_MONITOR, SET_LOADING } from '../type'



const MonitorState = props => {
    //初始化状态
    const initialState = {
        monitor: {},
        loading: false,
        total:0

    };

    //使用useReducer，即state关联Reducer
    const [state, dispatch] = useReducer(MonitorReducer, initialState) //函数参数1：当前提交的reduver       函数参数2：当前提交的状态值 ；

    //实现方法区域                                                          
    //getLoginUser
    const getMonitor = async () => { //根据login名获取user信息
        setLoading(true)//只要开始请求数据，更改为loading状态

        const res = await axios.get('/monitor/api/hosts/status',{})
        if(res.status!==200){
            message.info('网络错误')
            setLoading(false)
            return
        }
        // if(res.data.errno!==0){
        //     message.info('请求失败')
        //     setLoading(false)
        //     return
        // }
        dispatch({
            type: GET_MONITOR,
            payload: {
                monitor: res,
                total:res.length
            }
        })

        setLoading(false)
    };


    //set loading
    const setLoading = (loading) => {
        dispatch({
            type: SET_LOADING, //使用dispatch提交到Reducer中
            payload: { loading }
        })
    }

    return (<MonitorContext.Provider value={{
        monitor: state.monitor,
        loading: state.loading,
        total:state.total,

        setLoading,
        getMonitor,
    }}>{props.children}</MonitorContext.Provider>)


}
export default MonitorState
